import React from 'react';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { payOrder, fetchOrder } from '../../actions/orderActions';
import formatCurrency from '../../util';

const OrderContainer = props => {
  const dispatch = useDispatch();
  const orderDetails = useSelector(state => state.orders);
  const { success, order } = orderDetails;

  useEffect(() => {
    if (success) {
      orderDetails.success = false;
    }
    dispatch(fetchOrder(props.match.params.id));
    return () => {
      //
    };
  }, [dispatch, orderDetails.success, success, props.match.params.id]);

  const payHandler = () => {
    order.isPaid = true;
    order.paidAt = new Date();
    dispatch(payOrder(order));
  };

  return !order.id ? (
    <div>Loading...</div>
  ) : (
    <div>
      <div className='placeorder'>
        <div className='placeorder-info'>
          <div>
            <h2>Order Summary</h2>
          </div>
          <div>
            <h3>Shipping</h3>
            <div>
              {order.shipping.address}, {order.shipping.city}, {order.shipping.postalCode}, {order.shipping.country}
              <div>
                <br />
                <strong>{order.isDelivered ? `Delivered at ${order.deliveredAt}` : 'Not Delivered.'}</strong>
              </div>
            </div>
          </div>
          <div>
            <h3>Payment</h3>
            <div>Payment Method: {order.payment.paymentMethod}</div>
            <br />
            <div>
              <strong>{order.isPaid ? `Paid at ${order.paidAt}` : 'Not Paid.'}</strong>
            </div>
          </div>
          <div>
            <ul className='cart-list-container'>
              <li>
                <h3>Order Items</h3>
                <div>Price</div>
              </li>
              {order.cartItems.length === 0 ? (
                <div>Cart is empty</div>
              ) : (
                order.cartItems.map(item => (
                  <li key={item.product}>
                    <div className='cart-image'>
                      <img src={item.image} alt={item.title} />
                    </div>

                    <div className='cart-name'>
                      <div>
                        <Link to={`/products/${item.product}`}>{item.name}</Link>
                      </div>
                      <div>Qty: {item.qty}</div>
                    </div>
                    <div className='cart-price'>${item.price}</div>
                  </li>
                ))
              )}
            </ul>
          </div>
        </div>
        <div className='placeorder-action'>
          <ul>
            <li>
              {order.isPaid ? (
                <button disabled className='button primary full-width'>
                  Paid
                </button>
              ) : (
                <button className='button primary full-width' onClick={payHandler}>
                  Pay Now
                </button>
              )}
            </li>
            <li>
              <h3>Order Summary</h3>
            </li>
            <li>
              <div>Items</div>
              <div>{formatCurrency(order.itemsPrice)}</div>
            </li>
            <li>
              <div>Shipping</div>
              <div>{formatCurrency(order.shippingPrice)}</div>
            </li>
            <li>
              <div>Tax</div>
              <div>{formatCurrency(order.taxPrice)}</div>
            </li>
            <li>
              <div>Order Total</div>
              <div>{formatCurrency(order.totalPrice)}</div>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default OrderContainer;
